    // ))}

<table class="">
    <thead>
        <tr>
            <th>Name</th>
            <th>Type</th>
            <th>Actions </th>
        </tr>
    </thead>
    <tbody>
        {pets.map((pet, index) => {
            return (
            <tr key = {pet._id}>
                <td>{pet.petName}</td>
                <td>{pet.petType}</td>
                <td>
                    <Link className="link" to={`/pet/${pet._id}`}>
                    Pet Details
                    </Link>
                    <span> | </span>
                    <Link  className="link" to={`/pet/edit/${pet._id}`} state={pet}>
                    Update
                    </Link>
                </td>
                
            </tr>
            );
        })}
        
    </tbody>
</table>


    )
}

export default PetList;
