function Form(props) {
console.log('OLD PET', props.oldPet);
const [pet, setPet] = useState({
    petName: '',
    petType: '',
    petDescription: '',
    petTricks: "",
});

useEffect(() => {
    if (props.oldPet) {
    setPet(props.oldPet);
    }
}, [props.oldPet]);

const [errors, setErrors] = useState({});
const submitHandler = (e) => {
    e.preventDefault();
    props.submitHandler(pet, setErrors);
};

